import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css'; // Include your CSS file

const LoginPage = () => {
  const navigate = useNavigate();

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    // Handle login logic
    alert('Login successful!');
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-content">
          <h1 className="logo">Welcome</h1>
          <p className="tagline">Navigate your skills with ease</p>
          <form className="login-form" onSubmit={handleLoginSubmit}>
            <input type="email" id="email" placeholder="Email" required />
            <input type="password" id="password" placeholder="Password" required />
            <button type="submit" className="login-button">Login</button>
          </form>
          <div className="forgot-password">
            <a href="#">Forgot password?</a>
          </div>
          <div className="signup-option">
            <p>New here? <button onClick={() => navigate('/signup')} style={{ cursor: 'pointer' }}>Sign up</button></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
